import { React, useState, } from "react";
import { useNavigate } from "react-router-dom";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import { movieList } from "./App.js";

export default function Searchbar(){

    const [query, setQuery] = useState('');
    let searchResults = [];
    const navigate = useNavigate();

    const handleChange = (e) => {
        e.preventDefault();
        setQuery(e.target.value);
    };
    const onSubmit = (e) => {
        e.preventDefault();
        if (query === '') {
            searchResults = [];
            } else if (query !== '') {
            searchResults = movieList.filter(prod => prod.name.toLowerCase().includes(query.toLowerCase()))
        }
        navigate("/Searchresults")
    };

    // const handleKeyDown = (event) => {
    //     if (event.charCode === 13) {
    //         if (query === '') {
    //             setDisplayedProducts(originalProductList)
    //         }
    //     }
    //     navigate("/Products")
    // };

    return(
        <>
        <nav expand="lg">
            <Container fluid>
                <Form className="d-flex" onSubmit={onSubmit}>
                    <Form.Control role="search" type='search' value={query} placeholder="Search" onChange={handleChange} className="me-2" aria-label="Search" />
                    <Button variant="outline-success" type="submit">Submit</Button> 
                </Form>        
            </Container>
        </nav>
        </>

    )
}
